Giải thuật
 Danh sách a bắt đầu từ chỉ số 1 tới length
 Procedure insert (array a, int k, value) {
    int i:= k-1;
   while (i > 0 and a[i] > value) {
     a[i+1]:= a[i];
     i:= i - 1;
   }
   a[i+1]:= value;
 }

 Procedure InsertSort (array a, int length) {
   int k:= 2;
   while (k < length) {
     insert(a, k, a[k]);
     k:= k + 1;
   }
 }
// Mã giả viết bằng ngôn ngữ C++

 void InsertSort (int a[],int n)
 {
     int t,j;
     for(int i=1;i<n;i++)
     {
        j=i-1;
        t=a[i];
        while(j >= 0 && t < a[j])
        {
            a[j+1]=a[j];
            j--;
        }
        a[j+1]=t; // Chèn
     }  
 }

